import React from "react";
import { Table } from "react-bootstrap";
import { connect, useSelector } from "react-redux";

function Cart(props) {
  let state = useSelector((state) => state);
  console.log(state);

  return (
    <div>
      <div>
        <Table responsive="md">
          <thead>
            <tr>
              <th>#</th>
              <th>상품명</th>
              <th>수량</th>
              <th>변경</th>
            </tr>
          </thead>
          <tbody>
            {state.reducer.map((item, index) => {
              return (
                <tr key={index}>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.quan}</td>
                  <td>
                    <button
                      onClick={() => {
                        console.log(item.id);
                        props.dispatch({
                          type: "addQuan",
                          payload: { id: item.id },
                        });
                      }}
                    >
                      +
                    </button>
                    <button
                      onClick={() => {
                        props.dispatch({
                          type: "delQuan",
                          payload: { id: item.id },
                        });
                      }}
                    >
                      -
                    </button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
        {props.alert === true ? (
          <div className="my-alert-2">
            <p>지금 구매하시면 신규할인 20%</p>
            <button
              onClick={() => {
                props.dispatch({ type: "alertClose" });
              }}
            >
              닫기
            </button>
          </div>
        ) : null}
      </div>
    </div>
  );
}

