import React, { useContext, useState, lazy, Suspense, useEffect } from "react";
import { Navbar, Container, Nav, NavDropdown, Button } from "react-bootstrap";
import "./App.css";
import shoeList from "./data";
import styled from "styled-components";

import { Link, Route, Switch, useHistory } from "react-router-dom";

import axios from "axios";
import { useSelector } from "react-redux";
// import Detail from "./Detail.js";
let Detail = lazy(() => {
  return import("./Detail.js");
});

// import Cart from "./Cart.js";
let Cart = lazy(() => {
  return import("./Cart.js");
});

let Storage = lazy(() => {
  return import("./Storage.js");
});

let Pointer = styled.img`
  cursor: pointer;
`;

let TopTitle = styled.div`
  width: 250px;
  margin: 0 auto;
  color: red;
  padding: 30px 0 0;
  border-bottom: 3px solid red;
  margin-bottom: 20px;
`;

export let stockContext = React.createContext();
// 범위 생성(다른 파일의 컴포넌트에서 사용할 시 export 필요)

function App() {
  let [shoes, shoesChange] = useState(shoeList);
  let [stock, stockChange] = useState([10, 11, 12, 15, 14, 20]);
  let [recent, recentChange] = useState([]);

  // function dataAdd(data) {
  //   let newData = [...shoes];
  //   let addData = [...newData, ...data];
  //   shoesChange(addData);
  // }

  let state = useSelector((state) => {
    return state;
  });

  // localstorage 있으면 받아옴 없으면 패스
  useEffect(() => {
    let copy = [...state.reducer3];
    let storage = JSON.parse(localStorage.getItem("local"));
    let IntStorage = storage.map((i) => {
      return parseInt(i);
    });
    console.log(IntStorage);
    if (IntStorage) {
      copy = [...IntStorage];
    }
    state.reducer3 = copy;
  }, []);

  useEffect(() => {
    let copy = [...recent];
    let local = [...state.reducer3];
    console.log(local);
    if (local.length > 0) {
    }
    let newRecent = shoes.filter((i) => {
      return local.includes(i.id);
    });

    copy = newRecent;
    recentChange(copy);
  }, []);

  console.log(recent);

  return (
    <div className="App">
      <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand as={Link} to="/">
            ShoeShop
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              {/* <Nav.Link>
                <Link to="/">Home</Link>
              </Nav.Link> */}
              <Nav.Link as={Link} to="/">
                Home
              </Nav.Link>
              <Nav.Link as={Link} to="/detail/0">
                Detail
              </Nav.Link>
              <Nav.Link as={Link} to="/storage">
                Recently viewed
              </Nav.Link>
              <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.2">
                  Another action
                </NavDropdown.Item>
                <NavDropdown.Item href="#action/3.3">
                  Something
                </NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="#action/3.4">
                  Separated link
                </NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Switch태그로 감싸면 중복되는 url주소 있어도 상단에 작성된 하나만 보여줌 */}
      <Switch>
        <Route exact path="/">
          <div className="background">
            <h1>20% Season off</h1>
            <p>jumbotron</p>
            <p>
              <Button variant="primary">Primary</Button>
            </p>
          </div>
          <div className="container">
            {/* 범위 생성 html */}
            <stockContext.Provider value={stock}>
              <div className="row">
                {shoes.map((item, index) => {
                  return <ItemBox item={item} index={index} key={index} />;
                })}
              </div>
            </stockContext.Provider>
            <button
              className="btn btn-primary"
              onClick={() => {
                // 로딩중 UI

                // axios post 요청 = 데이터 전송
                // axios.post('서버URL', {id : "아이디", pw : 1234});

                // axios get요청
                axios
                  .get("https://codingapple1.github.io/shop/data2.json")
                  .then((result) => {
                    // 로딩중 UI 안보이게
                    //서버와 연결 성공했을때
                    // result는 성공해서 받아온 모든 정보들이 담겨있는 객체
                    shoesChange([...shoes, ...result.data]);
                    // result.data는 결과 객체에서 데이터 프로퍼타에 해당하는 값만 가져옴
                  })
                  .catch(() => {
                    //서버와 연결 실패했을때
                    console.log("실패!");
                  });
                //fetch도 위와 사용법은 거의 같음 대신 axios는 JSON파일의 "":"" 내용의 문자열에서
                // ""를 제거하여 객체 타입으로 반환해주고 fetch 함수는 JSON 그대로 반환하기때문에 객체타입으로 변환하는 작업이 필요하다.
                // fetch("https://codingapple1.github.io/shop/data2.json")
                //   .then((result) => {
                //     console.log(result.data);
                //   })
                //   .catch();
                // 위처럼 사용하면 undefined 출력됨, 객체타입으로 변환 필요
              }}
            >
              더보기
            </button>
          </div>
        </Route>
        <Route exact path="/detail/:id">
          <stockContext.Provider value={stock}>
            <Suspense fallback={<div>로딩중입니다...</div>}>
              <Detail
                shoes={shoes}
                shoesChange={shoesChange}
                stock={stock}
                stockChange={stockChange}
              />
            </Suspense>
          </stockContext.Provider>
        </Route>

        <Route path="/cart">
          <Suspense fallback={<div>로딩중입니다...</div>}>
            <Cart />
          </Suspense>
        </Route>

        <Route path="/storage">
          <Suspense fallback={<div>로딩중입니다...</div>}>
            <div className="container">
              <TopTitle>
                <h1>최근 본 상품</h1>
              </TopTitle>
              <stockContext.Provider value={stock}>
                <div className="row">
                  {shoes.map((item, index) => {
                    return (
                      <Storage
                        item={item}
                        index={index}
                        key={index}
                        stock={stock}
                        stockChange={stockChange}
                      />
                    );
                  })}
                </div>
              </stockContext.Provider>
            </div>
          </Suspense>
        </Route>

        <Route path="/:id">
          {/* :id 는 아무문자를 의미 */}
          <div>아무거나 적었을때 이거 보여줌</div>
        </Route>
      </Switch>
      {/* <Route path="~~~" component={~~~}></Route> */}
    </div>
  );
}

function ItemBox(props) {
  let history = useHistory();

  // state 공유 범위 사용 변수
  let stock = useContext(stockContext);

  return (
